def fu2(fn):    
    newName = ''.join(x for x in fn if x not in
    ',<>:""[]{}()/\|!@$%^&*,.?')    
    newName = newName.replace("-", " ")
    newName = newName.replace("_", " ")
    if len(newName) > 20:
        newName = newName.replace(" ", "")
    newName = newName[:18]
